import React, { useState } from 'react';
import './CSS/Login.css'
import { Redirect } from 'react-router-dom';

import {setEmail,setIsLoggedIn} from '../redux/actions/userActions';
// import 'bootstrap/dist/css/bootstrap.min.css';
const Login = () => {
    //states hook for testing purpose
    let [myEmail, setEmail] = useState('')
    const updateEmailTest = (email) => {
        setEmail(myEmail = email);
    };
    let [myPassword, setPassword] = useState('');
    const updatePassword = (password) => {
        setPassword(myPassword = password);
    };

    let[isLoggedIn,setIsLogged] = useState(false)

    const verify = () => {
        setIsLogged(true);
    }
    const updateEmail = (newEmail) => {
        if (newEmail.length < 20) {
            console.log(newEmail);
        }
    }

    if (isLoggedIn === true) {
        console.log("login test is set to true");
        return <Redirect to='/home'/>;
    }


    return (
       <div className="wallpaper">
           <div className="container">
               <div className="row">
                   <div className="col">
                   <h1>Algorithms quiz</h1>
                   </div>
               </div>
            <div className="row">
                <div className="col-1"></div>
                <div className="col">
                    <div className="Black-tranparent-box">
                    <h2 className="sign-in-text">Sign in</h2>
                        <div className="login ">
                            <div className>
                                {/* email input */}
                                <input
                                    class="form-control"
                                    type="text"
                                    placeholder="Username"
                                    value={myEmail}
                                    onChange={e => updateEmailTest(e.target.value)}
                                />
                            </div>

                            <div className="input-boxes">
                                {/* password input */}
                                <input
                                    class="form-control"
                                    type="password"
                                    placeholder="Password"
                                    // value={myPassword}
                                    onChange={e => updatePassword(e.target.value)}
                                />
                            </div>
                            <div  >
                                <button className="login-b" 
                                onClick = {verify}>
                                    Login
                                </button>
                                <div className="sign-up-b ">
                                    Sign-up
                                </div>
                            </div>
                        </div>

                    </div>
                </div>
                <div className="col-1"></div>
            </div>
        </div>
       </div>
    )
}
export default Login;
